export default (state, action) => {
  switch (action.type) {
    case "REMOVE_CONTACT":
      return {
        ...state,
        contacts: state.contacts.data.filter((contact) => {
          return contact.id !== action.payload;
        }),
      };
    case "GET_CONTACTS":
      return {
        ...state,
        contacts: action.payload,
      };
    case "ADD_CONTACT":
      console.log(state, action.payload);
      return {
        ...state,
        contacts: [action.payload, ...state.contacts.data],
      };
    case "EDIT_CONTACT":
      const updateContact = action.payload;
      const updateContacts = state.contacts.data.map((contact) => {
        if (contact.id === updateContact.id) {
          return updateContact;
        }
        return contact;
      });
      return {
        ...state,
        contacts: updateContacts,
      };

    default:
      return state;
  }
};
