import React, { createContext, useReducer, useEffect, useState } from "react";
import AppReducer from "./AppReducer";
import axios from "axios";

const initialState = {
  contacts: [],
};

export const GlobalContext = createContext(initialState);

// Provider Component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState.contacts);
  // Actions

  const getContacts = (contacts) => {
    dispatch({
      type: "SET_CONTACTS",
      payload: contacts,
    });
  };

  const removeContact = (id) => {
    dispatch({
      type: "REMOVE_CONTACT",
      payload: id,
    });
  };

  const addContact = (contact) => {
    dispatch({
      type: "ADD_CONTACT",
      payload: contact,
    });
  };

  const editContact = (contact) => {
    dispatch({
      type: "EDIT_CONTACT",
      payload: contact,
    });
  };

  useEffect(() => {
    axios
      .get("/contacts.json")
      .then((response) => {
        const { data } = response;
        getContacts(data);
      })

      .catch((error) => console.log(error));
  }, [state]);

  return (
    <GlobalContext.Provider
      value={{
        contacts: [],
        getContacts,
        removeContact,
        addContact,
        editContact,
      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};
