import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "../components/Card";

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [uniqueLetters, setUniqueLetters] = useState([]);
  const [filteredContacts, setFilteredContacts] = useState([]);
  const [searchText, setSearchText] = useState("");

  useEffect(async () => {
    const result = await axios.get("./contacts.json");
    setContacts(result.data.data);
  }, []);

  const getUniqueLetter = () => {
    for (var i = 0; i < contacts.length; i++) {
      return contacts[i].fullName;
    }
  };
  useEffect(() => {
    setUniqueLetters(getUniqueLetter);
  }, [contacts]);
  console.log(uniqueLetters);

  return (
    <div className="contact-list">
      <h1 className="title title_underline">Contacts</h1>
      <ul></ul>
      <div className="list-wrapper">
        {contacts.map((contactItem, i) => {
          return (
            <Card
              key={i}
              name={contactItem.fullName}
              email={contactItem.emailAddress}
              phone={contactItem.phoneNumber}
            ></Card>
          );
        })}
      </div>
    </div>
  );
};

export default ContactList;
