import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "../components/Card";
const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [uniqueLetters, setUniqueLetters] = useState([]);
  const [searchText, setSearchText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      axios.get("./contacts.json").then((response) => {
        setLoading(false);
        setContacts(response.data.data);
        for (var i = 0; i < contacts.length; i++) {
          console.log(contacts[1].fullName);
        }
        // const filter = response.data.data.reduce(
        //   (result, { fullName }) => [
        //     ...result,
        //     ...(!result.includes(fullName.charAt(0))
        //       ? [fullName.charAt(0)]
        //       : []),
        //   ],
        //   []
        // );

        // setUniqueLetters(filter);
      });
    }

    fetchData();
  }, []);

  const handleFilter = (event) => {
    const selectedLetter = event.currentTarget.innerText;
    var selectedContacts = contacts.filter(function (contactItem) {
      return contactItem.fullName.charAt(0) == selectedLetter;
    });
    setContacts(selectedContacts);
  };
  return (
    <div className="contact-list">
      <h1 className="title title_underline">Contacts</h1>
      <ul>
        {uniqueLetters.map((letter, i) => {
          return (
            <li key={i} onClick={handleFilter}>
              {letter}
            </li>
          );
        })}
      </ul>
      <div className="list-wrapper">
        {contacts.map((contactItem, i) => {
          return (
            <Card
              key={i}
              name={contactItem.fullName}
              email={contactItem.emailAddress}
              phone={contactItem.phoneNumber}
            ></Card>
          );
        })}
      </div>
    </div>
  );
};

export default ContactList;
