import React, { useEffect, useState, useContext } from "react";

import Title from "../components/Title";
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import { Link, useHistory } from "react-router-dom";
import { ContactsContext } from "../context/index";

const EditContact = (props) => {
  const { contacts, editContact } = useContext(ContactsContext);
  const [selectedContact, setSelectedContact] = useState({
    id: "",
    fullName: "",
    emailAddress: "",
    phoneNumber: "",
  });
  const history = useHistory();
  const currentContactId = props.match.params.id;

  useEffect(() => {
    if (!contacts) return;
    let contactList = contacts.data;
    const contactId = currentContactId;
    const selectedContact = contactList.find(
      (contact) => contact.id === contactId
    );
    setSelectedContact(selectedContact);
  }, [currentContactId, contacts]);

  const onChangeName = (e) => {
    setSelectedContact({
      ...selectedContact,
      [e.target.fullName]: e.target.value,
    });
  };
  const onChangephone = (e) => {
    setSelectedContact({
      ...selectedContact,
      [e.target.phoneNumber]: e.target.value,
    });
  };

  const onChangeemail = (e) => {
    setSelectedContact({
      ...selectedContact,
      [e.target.emailAddress]: e.target.value,
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    editContact(selectedContact);
    history.push("/");
  };
  return (
    <>
      <Title content="Edit Contact" />
      <Form onSubmit={onSubmit}>
        <FormGroup>
          <Label>Name</Label>
          <Input
            value={selectedContact.fullName}
            onChange={onChangeName}
            type="text"
          ></Input>
        </FormGroup>
        <FormGroup>
          <Label>Email Address</Label>
          <Input
            type="email"
            value={selectedContact.emailAddress}
            onChange={onChangeemail}
          ></Input>
        </FormGroup>
        <FormGroup>
          <Label>Phone Number</Label>
          <Input
            type="tel"
            value={selectedContact.phoneNumber}
            onChange={onChangephone}
          ></Input>
        </FormGroup>
        <Button type="submit">Edit Contact</Button>
      </Form>
    </>
  );
};

export default EditContact;
