import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import Card from "../components/Card";
import { GlobalContext } from "../context/GlobalState";
const ContactList = () => {
  const [uniqueLetters, setUniqueLetters] = useState([]);
  const [searchText, setSearchText] = useState("");
  const [loading, SetLoading] = useState(false);
  const { contacts, getContacts } = useContext(GlobalContext);

  const getUniqueLetter = () => {
    const filter = contacts.reduce(
      (result, { fullName }) => [
        ...result,
        ...(!result.includes(fullName.charAt(0)) ? [fullName.charAt(0)] : []),
      ],
      []
    );
    return filter;
  };
  const fetchData = () => {
    axios.get("./contacts.json").then(function (response) {
      setContacts(response.data.data);
      setUniqueLetters(getUniqueLetter());
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    setUniqueLetters(
      Array.from(new Set(contacts.map((name) => name.fullName.charAt(0))))
    );
  }, [contacts]);

  const handleFilter = (event) => {
    const selectedLetter = event.currentTarget.innerText;
    var selectedContacts = contacts.filter(function (contactItem) {
      return contactItem.fullName.charAt(0) == selectedLetter;
    });
    setContacts(selectedContacts);
  };
  const { data } = useContext(GlobalContext);
  console.log(data);

  return (
    <div className="contact-list">
      <ul>
        {uniqueLetters.map((letter, i) => {
          return (
            <li key={i} onClick={handleFilter}>
              {letter}
            </li>
          );
        })}
      </ul>
      <div className="list-wrapper">
        {contacts.map((contactItem, i) => {
          return (
            <Card
              key={i}
              userId={contactItem.id}
              name={contactItem.fullName}
              email={contactItem.emailAddress}
              phone={contactItem.phoneNumber}
            ></Card>
          );
        })}
      </div>
    </div>
  );
};

export default ContactList;
