import React, { useContext, useState } from "react";
import { useParams } from "react-router-dom";
import { useHistory } from "react-router-dom";

import Title from "../components/Title";
import { ListGroup, ListGroupItem, Button } from "reactstrap";
import { GlobalContext } from "../context/GlobalState";
import { Link } from "react-router-dom";

const ContactDetails = () => {
  let params = useParams();
  const [id, setId] = useState("");
  const { removeContact } = useContext(GlobalContext);
  const history = useHistory();

  const handleRemoveBtn = () => {
    removeContact(params.id);
    history.push("/");
  };
  return (
    <>
      <Title content="Profile" />
      <ListGroup>
        <ListGroupItem>
          <span>Name:</span>
          <span>Nagy David</span>
        </ListGroupItem>
        <ListGroupItem>
          <span>E-mail:</span>
          <span>david.nagy@nagydavid.com</span>
        </ListGroupItem>
        <ListGroupItem>
          <span>Phone:</span>
          <span>+3620394420</span>
        </ListGroupItem>
      </ListGroup>
      <div>
        <Button outline onClick={handleRemoveBtn} color="danger">
          Delete
        </Button>

        <Link to={`/edit/${id}`} outline color="secondary">
          Edit
        </Link>
      </div>
    </>
  );
};

export default ContactDetails;
