import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "../components/Card";
import Title from "../components/Title";
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import { Link, useHistory } from "react-router-dom";

const EditContact = (props) => {
  const { contacts, setContacts } = useContext(ContactsContext);
  const [selectedContact, setSelectedCOntact] = useState({
    id: "",
    fullName: "",
    emailAddress: "",
    phoneNumber: "",
  });
  const history = useHistory();
  const currentContactId = props.match.params.id;

  useEffect(() => {
    const contactId = currentContactId;
    const selectedContact = contacts.find(
      (contact) => contact.id === contactId
    );
    setSelectedContact(selectedContact);
  }, [currentContactId, contacts]);

  const onChange = (e) => {
    setSelectedContact({
      ...selectedContact,
      [e.target.fullName]: e.target.value,
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setContacts(selectedContact);
    history.push("/");
  };
  return (
    <>
      <Title content="Edit Contact" />
      <Form onSubmit={onSubmit}>
        <FormGroup>
          <Label>Name</Label>
          <Input
            value={selectedContact.fullName}
            onChange={onChange}
            type="text"
          ></Input>
        </FormGroup>
        <FormGroup>
          <Label>Email Address</Label>
          <Input type="email" placeholder="Enter Email"></Input>
        </FormGroup>
        <FormGroup>
          <Label>Phone Number</Label>
          <Input type="tel" placeholder="Enter Phone"></Input>
        </FormGroup>
        <Button type="submit">Add Contact</Button>
      </Form>
    </>
  );
};

export default EditContact;
