import React, { useContext, useState } from "react";
import { useParams } from "react-router-dom";
import Title from "../components/Title";
import { ListGroup, ListGroupItem, Button } from "reactstrap";
import { Link } from "react-router-dom";
import { ContactsContext } from "../context/index";

const ContactDetails = () => {
  const { contacts } = useContext(ContactsContext);
  const [selectedContact, setSelectedContact] = useState({
    id: "",
    fullName: "",
    emailAddress: "",
    phoneNumber: "",
  });
  const history = useHistory();
  const currentContactId = props.match.params.id;
  useEffect(() => {
    if (!contacts) return;
    let contactList = contacts.data;
    const contactId = currentContactId;
    const selectedContact = contactList.find(
      (contact) => contact.id === contactId
    );
    console.log(selectedContact);
    setSelectedContact(selectedContact);
  }, [currentContactId, contacts]);
  return (
    <>
      <Title content="Profile" />
      <ListGroup>
        <ListGroupItem>
          <span>Name:</span>
          <span>Nagy David</span>
        </ListGroupItem>
        <ListGroupItem>
          <span>E-mail:</span>
          <span>david.nagy@nagydavid.com</span>
        </ListGroupItem>
        <ListGroupItem>
          <span>Phone:</span>
          <span>+3620394420</span>
        </ListGroupItem>
      </ListGroup>
      <div>
        {/* <Button outline onClick={() => removeContact(params.id)} color="danger">
          Delete
        </Button> */}

        <Link to={`/edit/${params.id}`}>Edit</Link>
      </div>
    </>
  );
};

export default ContactDetails;
